import React, { Fragment, useState } from "react";

export default function TodoInput() {
	const [description, setDescription] = useState("New todo...");

	return (
		<Fragment>
			<h1>input todo</h1>
			<from>
				<input type="text" value={description} />
				<button>Add</button>
			</from>
		</Fragment>
	);
}
