import React, { Fragment, useState } from "react";

export default function TodoInput() {
	const [description, setDescription] = useState("");

	return (
		<Fragment>
			<h1>input todo</h1>
			<from>
				<input
					type="text"
					value={description}
					onChange={(e) => {
						setDescription(e.target.value);
					}}
				/>
				<button>Add</button>
			</from>
		</Fragment>
	);
}
